import Navigation from '@/components/Navigation.vue'
import Sidebar from '@/components/Sidebar.vue'
import Loader from '@/components/Loader.vue'
import TableList from '@/components/TableList.vue'
import FormBuilder from '@/components/FormBuilder.vue'
import axios from 'axios';
export default {
  name: 'terminals',
  mounted () {
    
  },
  data:
    function() {
        return{
              syncing: true,
              showModal: false,
              showModalUpdate: false,
              loading: false,
              loadingSave: false,
              warningMessage: '',
              isValidPhoto: true,
              tableName: "terminals",
              photoUrl: '',
              file: '',
              showFields: {
                  "orderBy": "updatedAt",
                  "orientation": "desc",
                  "list": ['key','status','orderedAt']
              },
              iconButtons: [
                {
                  name: "use",
                  uiName: "fas fa-pen",
                  callName: "retrieveData",
                  accepting: ["hashId"]
                },
                {  
                  name: "use3",
                  uiName: "fas fa-times",
                  callName: "delete",
                  accepting: ["hashId"]
                }
              ],
              btnClose: [
                {
                  name: "btnClose",
                  uiName: "Cancel",
                  callName: "closeModal",
                }
              ],
              btnAdd:[{
                name:"btnAdd",
                callName: "btnAddModal"
              }],
              
              cardButton: [
                {
                  name: "use",
                  uiName: "Create",
                  callName: "postData",
                  className: "btn btn-primary"
                }
              ],
             cardButtonUpdate: [
                {
                  name: "use",
                  uiName: "Save",
                  callName: "putData",
                  className: "btn btn-primary"
                }
              ],
              form: [
                  {
                  name: "currentVersion",
                  labelName: "Current Version:",
                  inputId: "currentVersion",
                  cardInputCol: "col-md-6",
                  max: 31,
                  ifRequired: true,
                  inputType: "number",
                  errorName: "Please input Current Version"
                },
                  {
                  name: "terminalId",
                  labelName: "Terminal ID.:",
                  inputId: "terminalId",
                  cardInputCol: "col-md-6",
                  max: 31,
                  ifRequired: true,
                  inputType: "number",
                  errorName: "Please input Terminal ID."
                },
                {
                  name: "serialNo",
                  labelName: "Serial No.:",
                  inputId: "serialNo",
                  cardInputCol: "col-md-6",
                  max: 31,
                  ifRequired: true,
                  inputType: "number",
                  errorName: "Please input Serial No."
                },
                 {
                  name: "tin",
                  labelName: "Tin No.:",
                  inputId: "tin",
                  cardInputCol: "col-md-6",
                  max: 30,
                  ifRequired: true,
                  inputType: "number",
                  errorName: "Please input TIN No."
                },
                {
                  name: "licenseKey",
                  labelName: "License Key:",
                  inputId: "licenseKey",
                  cardInputCol: "col-md-6",
                  max: 30,
                  ifRequired: true,
                  inputType: "varchar",
                  errorName: "Please input License Key"
                },
                 {
                  name: "permitNo",
                  labelName: "Permit No.:",
                  inputId: "permitNo",
                  cardInputCol: "col-md-6",
                  max: 30,
                  ifRequired: true,
                  inputType: "varchar",
                  errorName: "Please input Permit No."
                },
                 {
                  name: "machineIdentificationNo",
                  labelName: "Machine ID No.:",
                  inputId: "machineIdentificationNo",
                  cardInputCol: "col-md-6",
                  max: 30,
                  ifRequired: true,
                  inputType: "varchar",
                  errorName: "Please input Machine ID No."
                },
                 {
                  name: "accreditationNo",
                  labelName: "Accrediation No.:",
                  inputId: "accreditationNo",
                  cardInputCol: "col-md-6",
                  max: 30,
                  ifRequired: true,
                  inputType: "varchar",
                  errorName: "Please input Accrediation No."
                }
              ],
          
               formSelect: [{
                  labelName: "Branches:",
                  name: "branchHashId",
                  formSelectCol: "col-md-6",
                  inputType: "select",
                  callName: "selectBranch",
                  errorName: "Please select Branch",
                  options: JSON.parse(localStorage.getItem('listOfBranches')),
                  defaultPlaceholder: "Branches",
                  selectId: "branchHashId",
                  className: "select form-control",
                  ifRequired: true
               }],
              iconColor: [
                 {
                     color: '#CBCBCB'
                 }
              ],
            indexedFilter:  {
              status: "A"
            }
        }
    },
  components: {
      Navigation,
      Loader,
      FormBuilder,
      Sidebar,
      TableList
  },
  methods: {
        stopSync: function () {
            this.syncing = false
        },
        closeModal: function(){
            this.$store.state.showModal = false
            this.$store.state.showModalUpdate = false
            this.$store.state.loading = false
        },
        openModal: function (){
            this.$store.state.showModal = true
        },
        openModalUpdate: function(){
             this.$store.state.showModalUpdate = true
        }
  },
  watch:{
  },
  computed: {
  }

}
